<?phpif ( !empty( $_SERVER['SCRIPT_FILENAME'] ) && 'comments.php' == basename( $_SERVER['SCRIPT_FILENAME'] ) )    die ( 'Please do not load this page directly. Thanks!' );if ( post_password_required() ) {    ?>    <p><em><?php _e( 'This post is password protected. Enter the password to view comments.', 'health-plus' ); ?></em></p>    <?php    return;}?><div class="col-sm-11 step-right">    <div class="comments">        <h2><?php _e( 'Comments', 'health-plus' )?></h2>        <?php if ( have_comments() ) : ?>            <ul>                <?php wp_list_comments( 'type=comment&callback=hnm_comments' ); ?>            </ul>            <div class="col-sm-11 pager-section">                <?php paginate_comments_links() ?>            </div>        <?php else : ?>            <?php if ( comments_open() ) : ?>                <p><em><?php _e( 'Sorry no comment yet.', 'health-plus' ); ?></em></p>            <?php else : ?>                <p><em><?php _e( 'Comments are closed.', 'health-plus' ); ?></em></p>            <?php endif; ?>        <?php endif; ?>    </div></div>