<?phpfunction is_blog() {    global $post;    $post_type = get_post_type( $post );    return ( ( ( is_archive() ) || ( is_author() ) || ( is_category() ) || ( is_home() ) || ( is_single() ) || ( is_tag() ) || ( is_search() ) ) && ( $post_type == 'post' ) ) ? true : false ;}function hnm_body_classes( $classes ) {    global $hnmOpt;    if( isset( $hnmOpt['site_layout'] ) && !empty( $hnmOpt['site_layout'] ) )    {        $classes[] = $hnmOpt['site_layout'];    }    else    {        $classes[] = 'var2';    }    if( is_page_template('template-home.php') | is_page_template('template-home-b.php') || is_page_template('template-home-c.php') )    {        $classes[] = 'home';    }    return $classes;}add_filter( 'body_class', 'hnm_body_classes' );function hnm_active_menu( $css_classes, $item ) {    $css_classes = str_replace('current_page_item', 'active', $css_classes);    $css_classes = str_replace('current-menu-item', 'active', $css_classes);    $css_classes = str_replace('current-post-ancestor', 'active', $css_classes);    $css_classes = str_replace('current-menu-parent', 'active', $css_classes);    $css_classes = str_replace('current-menu-ancestor', 'active', $css_classes);    return $css_classes;}add_filter( 'nav_menu_css_class', 'hnm_active_menu', 10, 2 );function hnm_excerpt_hellip() {    return ' &hellip;';}add_filter( 'excerpt_more', 'hnm_excerpt_hellip' );function hnm_get_excerpt( $limit ) {    $excerpt = get_the_excerpt();    $limit++;    if ( mb_strlen( $excerpt ) > $limit ) {        $subex = mb_substr( $excerpt, 0, $limit - 5 );        $exwords = explode( ' ', $subex );        $excut = - ( mb_strlen( $exwords[ count( $exwords ) - 1 ] ) );        if ( $excut < 0 ) {            return mb_substr( $subex, 0, $excut ) . ' ...';        } else {            return $subex . ' ...';        }    } else {        return $excerpt;    }}if( !function_exists( 'hnm_comments' ) ){    function hnm_comments($comment, $args, $depth) {        $GLOBALS['comment'] = $comment;        ?>        <li id="comment-<?php comment_ID() ?>">            <div class="comment-content">                <div class="col-sm-1 pad0">                    <?php echo get_avatar( $comment->comment_author_email, 67 ); ?>                </div>                <div class="col-sm-11">                    <h3><?php comment_author_link(); ?><span><?php echo get_comment_date( 'F j, Y' ); ?></span></h3>                    <?php comment_text(); ?>                    <?php comment_reply_link( array_merge( $args, array( 'reply_text' => __( 'Reply', 'health-plus' ), 'depth' => 3, 'max_depth' => $args['max_depth'] ) ) ) ?>                </div>            </div>        <?php    }}